import React, {useState} from "react";

import {useParams} from "react-router-dom";

export default function GameField(){

  function drawFigure(index){
    console.log("index: " + index);
    const tempGameState = gameState.map((boardCell, i) => {
      return i === index ? {...boardCell, figure: "X"}: boardCell;
    });
    setGameState(tempGameState);
  }

  let params = useParams();
  let gameId = params.gameId;
  const [gameState, setGameState] = useState(new Array(9));
  return (
    <>
      <h4>Welcome from game field: {gameId}</h4>
      <div id = "gameBoardContainer">
        <div id = "gameBoard">
          {gameState.map((boardCell, i) => {
            return (
              <div key = {i} onClick = {() => drawFigure(i)}>
                {boardCell !== undefined ? boardCell.figure : ""}
              </div>
            )
          })}
