import {io} from "socket.io-client";

//class with methods for interacting with the sockets on the server
let SocketService = {};

SocketService.prototype.connect = function (url){
  //connects to server and returns socket instance
  return new Promise((resolve, reject) => {
    this.socket = io(url);

    if(!this.socket){
      reject(new Error("socket not created!"));
    }

    this.socket.on("connect", () => {
      console.log("Made connection");
      resolve(this.socket);
    });

    this.socket.on("connect_error", (err) => {
      console.log("Didn't make connection");
      reject(new Error(err));
    });
  });
}



export default SocketService;
