import React, {useEffect, useState} from "react";
import logo from './logo.svg';
import './App.css';
import {io} from "socket.io-client";
import {Routes, Route, useNavigate} from "react-router-dom";

import JoinGame from "./components/JoinGame";
import GameField from "./components/GameField";
import SocketService from "./services/SocketService";

var socket;

function App() {

  const setUpListeners = (socket) => {
    //Listening for establishing connection with server
    socket.on("connect", () => {
      console.log("Made connection");
    });

    socket.on("connect_error", (err) => {
      console.log("Didn't make connection: " + err);
    });

    //Listening for successfully joining the socket's room
    socket.on("join_room_success", (message) => {
      console.log("Successfully joined the room " + message.roomId);
      setRoomId(message.roomId);
    });

    //Listening for error while joining the socket's room
    socket.on("join_room_error", (message) => {
      alert(message.error);
    });
  }

  function drawFigure(index){
    console.log("index: " + index);
    let abortDrawing = false;
    const tempGameState = gameState.map((boardCell, i) => {
      if(i === index){
        if(boardCell === ""){

          return "X";
        }else{
          alert("Can't reassign a cell");
          abortDrawing = true;
          return boardCell;
        }
      }else{
        return boardCell;
      }
    });
    if(!abortDrawing){
      setGameState(tempGameState);
    }
  }

  function cleanBoard(){
    let arr = new Array(9);
    for(let i = 0; i < arr.length; i++){
      arr[i] = "";
    }
    setGameState(arr);
  }

  let navigate = useNavigate();
  const [gameId, setGameId] = useState("");
  const [roomId, setRoomId] = useState("");
  let arr = new Array(9);
  for(let i = 0; i < arr.length; i++){
    arr[i] = "";
  }
  const [gameState, setGameState] = useState(arr);


  const connect = () => {
    socket = io("http://localhost:9002");
    setUpListeners(socket);
  }

  // const connectSocket = async () => {
  //   socket = await SocketService.connect("http://localhost:9002").catch((err) => {
  //     console.log("Error: ", err);
  //   }, []);
  // }

  useEffect(() => {
    connect();
    //---OR---//
    // SocketService.connect("http://localhost:9002").then((result) => {
    //   socket = result;
    // });
    //---OR---//
    // connectSocket();
  }, []);

  const submitForm = (e) => {
    e.preventDefault();
    if(gameId !== ""){
      if(socket){
        socket.emit("join_game", {"roomId": gameId});
      }
      navigate("/game/" + gameId);
      setGameId("");
    }else{
      alert("Enter the id of the room");
    }
  }

  const changeGameId = (e) => {
    setGameId(e.target.value);
  }

  return (
    <>
      <Routes>
        <Route path = "/" element = {<JoinGame gameId = {gameId} functions = {{submitForm, changeGameId}}/>}/>
        <Route path = "/game/:gameId" element = {<GameField gameState = {gameState} functions = {{drawFigure, cleanBoard}}/>}/>
      </Routes>

    </>
  );
}

export default App;
